import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Header from "../../components/Header";
import Sidebar from "../../components/Sidebar";
import Footer from "../../components/Footer";

function AddCategory() {
  const initialState = {
    name: "",
  };

  const [data, setCategoriesData] = useState([]);
  const [state, setState] = useState(initialState);

  const addCategory = async (category) => {
    try {
      const response = await axios.post("http://localhost:8090/api/create-category", category);
      if (response.status === 200) {
        toast.success("Category added successfully.");
        // Clear the form after successful submission
        setState(initialState);
        // Refresh the list of categories after adding a new one
        getCategories();
      } else {
        toast.error("Failed to add category.");
      }
    } catch (error) {
      toast.error("An error occurred while adding the category.");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!state.name) {
      console.log("Error: name is empty");
      toast.error("Category name is required.");
    } else {
      addCategory(state);
    }
  };

  const handleInputChange = (e) => {
    let { name, value } = e.target;
    setState({ ...state, [name]: value });
  };

  useEffect(() => {
    getCategories();
  }, []);

  const getCategories = async () => {
    try {
      const response = await axios.get("http://localhost:8090/api/categories");
      if (response.status === 200) {
        setCategoriesData(response.data.data);
      }
    } catch (error) {
      toast.error("Failed to fetch categories.");
    }
  };

  return (
    <div>
      <Header />
      <Sidebar />
      <div className="content-wrapper">
        <ToastContainer position="top-right" />
        <section className="content-header">
          {/* ... */}
        </section>

        <section className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-md-4">
                <div className="card card-primary">
                  <div className="card-header">
                    <h3 className="card-title">Categories</h3>
                  </div>
                  <form onSubmit={handleSubmit}>
                    <div className="card-body">
                      <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Nom de la categorie</label>
                        <input
                          type="text"
                          className="form-control"
                          name="name"
                          id="name"
                          placeholder="Entrer la categorie"
                          onChange={handleInputChange}
                          value={state.name} 
                        />
                      </div>
                    </div>
                    <div className="card-footer">
                      <button type="submit" className="btn btn-primary">Enregistrer</button>
                    </div>
                  </form>
                </div>
              </div>
              {/* ... */}
            </div>
          </div>
        </section>

      </div>
      <Footer />
    </div>
  );
}

export default AddCategory;
