import Header from "../../components/Header"
import Sidebar from "../../components/Sidebar"
import Footer from "../../components/Footer"
import { NavLink } from "react-router-dom";
import React, {useState, useEffect} from "react";
import axios from "axios";
import { toast } from 'react-toast'

function AddCategory(){

      const initialState = {
            name: "",
      }
      const [data, setCategoriesData] = useState([]);
      const [state, setState] = useState(initialState);
      const {name} = initialState;
      const addCategory = async (data)=>{
            const response = await axios.post("http://localhost:8090/api/create-category", data)
            if(response.status === 200){
                  toast.success("Category add successfull.")
            }else{
                  console.log(response.data);
            } 
      }

      const handleSubmit = (e) =>{
            if(!name){
                  toast.success("Category name is .")  
            }
            e.preventDefault();
            addCategory(state);
      }

      const handleInputChange = (e) =>{
            let {name, value} = e.target;

            setState({...state, [name] : value})
      }
      useEffect(() => {
            getCategories();
      }, [])

      const getCategories = async () =>{
            const response = await axios.get("http://localhost:8090/api/categories");
            if(response.status === 200){
                  setCategoriesData(response.data.data);
            }
      }
      return (
         <div>
            <Header/>
            <Sidebar/>
            <div className="content-wrapper">
                        <section className="content-header">
                              <div className="container-fluid">
                              <div className="row mb-2">
                                    <div className="col-sm-6">
                                    <h1>General Form</h1>
                                    </div>
                                    <div className="col-sm-6">
                                    <ol className="breadcrumb float-sm-right">
                                    <li className="breadcrumb-item"><NavLink to="/">Dashboard</NavLink></li>
                                    <li className="breadcrumb-item active">Category Register</li>
                                    </ol>
                                    </div>
                              </div>
                              </div>{/* /.container-fluid */}
                        </section>

                        <section className="content">
                              <div className="container-fluid">
                                    <div className="row">
                                          <div className="col-md-4">
                                          <div className="card card-primary">
                                          <div className="card-header">
                                                <h3 className="card-title">Categories</h3>
                                          </div>
                                          {/* /.card-header */}
                                          {/* form start */}
                                          <form onSubmit={handleSubmit}>
                                                <div className="card-body">
                                                      <div className="form-group">
                                                            <label htmlFor="exampleInputEmail1">Nom de la categorie</label>
                                                            <input type="text" className="form-control" 
                                                            name="name" id="name" placeholder="Enter la categorie" 
                                                            onChange={handleInputChange} value={name}
                                                            />
                                                      </div>
                                                </div>
                                                {/* /.card-body */}
                                                <div className="card-footer">
                                                <input type="submit" className="btn btn-primary">Enregistrer</input>
                                                </div>
                                          </form>
                                          </div>
                                          </div>
                                          <div className="col-md-8">
                                          <div className="card">
                                                <div className="card-header">
                                                <h3 className="card-title">Categories List</h3>
                                                </div>
                                                {/* /.card-header */}
                                                <div className="card-body">
                                                <table className="table table-bordered">
                                                      <thead>
                                                            <tr>
                                                                  <th style={{width: 10}}>#</th>
                                                                  <th>Category name</th>
                                                                  <th>Actions</th>
                                                            </tr>
                                                      </thead>
                                                      <tbody>
                                                            {data && Array.isArray(data) && data.map((item, index) => {
                                                                  return (

                                                                        <tr key={index}>
                                                                              <th scope="row"> {index+1} </th>
                                                                              <td> {item.name} </td>
                                                                              <td>
                                                                                    <div className="row ">
                                                                                          <button className=" btn btn-warning mr-2">Mettre à jour</button>
                                                                                          <button className=" btn btn-danger">Supprimer</button>
                                                                                    </div>

                                                                                    {/* <div className="row ">
                                                                                          <Link to={"/category/edit/${item._id}"}>
                                                                                          <button className=" btn btn-warning mr-2">Mettre à jour</button>
                                                                                          </Link>
                                                                                          <Link to={}>
                                                                                                <button className=" btn btn-danger">Supprimer</button>
                                                                                          </Link>
                                                                                    </div> */}
                                                                              </td>
                                                                        </tr>
                                                                  );
                                                            })}
                                                      </tbody>
                                                </table>
                                                </div>
                                          {/* /.card-body */}
                                          </div>

                                          </div>
                                    </div>
                              </div>
                        </section>

                  </div>
            <Footer/>
         </div>   
      );
}

export default AddCategory;
